import React from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import { useState } from "react";

function create(className, submitToServer) {
    const result = submitToServer(className);
    console.log(result);
}

export default function CreateClassPopup(props) {
    const [input, setInput] = useState("");
    const {handleSubmission, ...restProps} = props;
    return (
        <Modal {...restProps} centered>
        <Modal.Header closeButton>
            <Modal.Title className="popupTitle" id="contained-modal-title-vcenter">
            Create New Class
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <h5>Name your class:</h5>
            <form >
            <input value={input} onInput={e => setInput(e.target.value)} name="name" />
            <Button id="formBtn" variant="primary" type="submit" onClick={() => create(input, handleSubmission)}>
                Create
            </Button>
            </form>
        </Modal.Body>
        </Modal>
    );
}
