import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

const App = () => {
  const [studentVerified, setStudentVerified] = useState('');
  const [studentName, setStudentName] = useState(''); 
  const [studentPassword, setStudentPassword] = useState(''); 

  useEffect(() => {
    const socket = io('http://localhost:3000');

    socket.on("connect", () => {
      console.log("You connected with id", socket.id);
    });



    // Cleanup the effect
    return () => socket.disconnect();
  }, []); // Removed studentName from the dependency array to avoid reconnecting on name change

  const handleNameChange = (e) => {
    setStudentName(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setStudentPassword(e.target.value);
  };

  const handleSubmit = () => {
    const socket = io('http://localhost:3000');
    socket.emit('studentInfo', { name: studentName, password: studentPassword });

    socket.on("studentVerification", (verificationStatus) => {
      console.log(verificationStatus);
      setStudentVerified(verificationStatus);
    });
  };

  return (
    <div>
      <h1>Language Master</h1>
      <label>
        Enter Name:
        <input type="text" value={studentName} onChange={handleNameChange} />
      </label>
      <label>
        Enter Password:
        <input type="text" value={studentPassword} onChange={handlePasswordChange} />
      </label>
      <button onClick={handleSubmit}>Submit</button>
      <h2>{studentVerified ? 'Student Verified' : 'Student Not Verified'}</h2>
    </div>
  );
};
export default App;
