import React, { useState } from 'react';
import { verifyStudent } from './socket';

const Login = () => {
  const [studentVerified, setStudentVerified] = useState(false);
  const [studentName, setStudentName] = useState(''); 
  const [studentPassword, setStudentPassword] = useState('');
  const [studentPassword1, setStudentPassword1] = useState('');


  const handleNameChange = (e) => {
    setStudentName(e.target.value);
    setStudentVerified(false); // Reset verification state when name is changed
  };

  const handlePasswordChange = (e) => {
    setStudentPassword(e.target.value);
    setStudentVerified(false); // Reset verification state when password is changed
  };

  const handleSubmit = () => {
    verifyStudent(studentName, studentPassword, (verificationStatus) => {
      console.log(verificationStatus);
      setStudentVerified(verificationStatus);
    });
  };

  return (
    <div>
      <div>
        <label>
          Enter Email:
          <input type="text" value={studentName} onChange={handleNameChange} />
        </label>
      </div>
      <div>
        <label>
          Create Password:
          <input type="password" value={studentPassword} onChange={handlePasswordChange} />
        </label>
      <div>
        <label>
          Re-enter Password:
          <input type="password" value={studentPassword1} onChange={handlePasswordChange} />
        </label>
        </div>

      </div>
      <button className="submit-button" onClick={handleSubmit}>Sign up as Teacher</button>
      <h2>{studentVerified ? "Sign up Successful!": ''}</h2>
    </div>
  );
};

export default Login;
