import React from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";

function create(formData, submitToServer) {
    const className = formData.get("name");
    const result = submitToServer(className);
    console.log(result);
}

export default function CreateClassPopup({show, onHide, handleSubmission}) {
  return (
    <Modal show onHide centered>
      <Modal.Header closeButton>
        <Modal.Title className="popupTitle" id="contained-modal-title-vcenter">
          Create New Class
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <h5>Name your class:</h5>
        <form action={(formData) => create(formData, handleSubmission)}>
          <input name="name" />
          <Button id="formBtn" variant="primary" type="submit">
            Create
          </Button>
        </form>
      </Modal.Body>
    </Modal>
  );
}
