import React, { useState } from 'react';
import { createStudent } from './socket';

const Login = () => {
  const [studentVerified, setStudentVerified] = useState(false);
  const [studentEmail, setStudentEmail] = useState(''); 
  const [studentPassword, setStudentPassword] = useState('');
  const [studentConfirmPassword, setStudentConfirmPassword] = useState('');
  const [studentFirstName, setStudentFirstName] = useState('');
  const [studentLastName, setStudentLastName] = useState('');

  const firstNameChange = (e) => {
    setStudentFirstName(e.target.value);
  };

  const lastNameChange = (e) => {
    setStudentLastName(e.target.value);
  };

  const handleEmailChange = (e) => {
    setStudentEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setStudentPassword(e.target.value);
  };

  const handleConfirmPasswordChange = (e) => {
    setStudentConfirmPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    // e.preventDefault(); // to prevent the default form submit action
    // if (studentPassword !== studentConfirmPassword) {
    //   alert("Passwords do not match.");
    //   return;
    // }
    createStudent(studentFirstName, studentLastName, studentEmail, studentPassword, (createStatus) => {
      console.log(studentFirstName, studentLastName, studentEmail, studentPassword)
      console.log("Student Created returned");
      if (createStatus) { // assuming createStatus indicates success
        setStudentVerified(true);
      }
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>
          Enter First Name:
          <input type="text" value={studentFirstName} onChange={firstNameChange} />
        </label>
      </div>
      <div>
        <label>
          Enter Last Name:
          <input type="text" value={studentLastName} onChange={lastNameChange} />
        </label>
      </div>
      <div>
        <label>
          Enter Email:
          <input type="email" value={studentEmail} onChange={handleEmailChange} />
        </label>
      </div>
      <div>
        <label>
          Create Password:
          <input type="password" value={studentPassword} onChange={handlePasswordChange} />
        </label>
      </div>
      <div>
        <label>
          Re-enter Password:
          <input type="password" value={studentConfirmPassword} onChange={handleConfirmPasswordChange} />
        </label>
      </div>
      <button type="submit" className="submit-button">Sign up as Teacher</button>
      <h2>{studentVerified ? "Sign up Successful!" : ''}</h2>
    </form>
  );
};

export default Login;
